mapper.insert:
insert into parti_tb values (
	#{gongguNo}, #{partiMember}, #{num}, sysdate, default
)

mapper.update:
update member
set point=point-#{totalPoint}
where user_id=#{userId}

update parti_tb
set status=2
where gonggu_no=#{gongguNo} and parti_member=#{userId}

update member
set point=point+#{price}
where user_id=#{gongguWriter}
